<template>
  <div id="coupons" style="background-color: #f7f7f7">
    <nav-bar title="优惠券市场" fixed  :zIndex="10"/>
    <div class="coupons-container">
      <en-empty v-if="finished && !couponsList.length">暂无优惠券</en-empty>
      <van-list
        v-else
        v-model="loading"
        :finished="finished"
        @load="onLoad"
      >
        <a
          v-for="(coupon, index) in couponsList"
          :key="index"
          href="javascript:;"
          class="item-coupon"
          :class="[coupon.create_num === coupon.received_num && 'brought-out']"
        >
          <div class="inner-coupon">
            <img class="img-coupon" src="data:image/png;base64,">
            <p class="name-coupon">
              <span>{{ coupon.title }}</span>
            </p>
            <span class="price-coupon">
              <em>¥{{ coupon.coupon_price }}</em>满{{ coupon.coupon_threshold_price }}可用
            </span>
            <span class="shop-name">
              <em :title="coupon.seller_name">所属店铺：<nuxt-link :to="'/shop/' + coupon.seller_id">{{ coupon.seller_name }}</nuxt-link></em>
            </span>
            <div class="receive-coupon" @click="receiveCoupon(coupon)">
	            <span v-if="coupon.create_num === coupon.received_num" class="icon-receive-coupon-out">已领完</span>
	            <span v-else class="icon-receive-coupon">立即<br>领取</span>
            </div>
          </div>
        </a>
      </van-list>
    </div>
  </div>
</template>

<script>
  import * as API_promotions from '@/api/promotions'
  import * as API_Members from '@/api/members'
  import Storage from '@/utils/storage'
  export default {
    name: 'coupons',
    head() {
      return {
        title: `优惠券市场-${this.site.title}`
      }
    },
    data() {
      return {
        loading: false,
        finished: false,
        params: {
          page_no: 0,
          page_size: 10
        },
        couponsList: []
      }
    },
    methods: {
      /** 加载数据 */
      onLoad() {
        this.params.page_no += 1
        this.GET_Coupons()
      },
      /** 领取优惠券 */
      receiveCoupon(coupon) {
        if (!Storage.getItem('refresh_token')) {
          this.$confirm('您还未登录，要现在去登录吗？', () => {
            this.$router.push({ path: '/login', query: {forward: `${this.$route.path}`} })
          })
          return false
        }
        API_Members.receiveCoupons(coupon.coupon_id).then(() => {
          this.$message.success('领取成功！')
        })
      },
      /** 获取优惠券列表 */
      GET_Coupons() {
        this.loading = true
        API_promotions.getAllCoupons(this.params).then(response => {
          this.loading = false
          const { data } = response
          if (!data || !data.length) {
            this.finished = true
          } else {
            this.couponsList.push(...data)
          }
        })
      }
    }
  }
</script>

